import React, { Component, Fragment } from "react";
import Task from "./Task/Task";
import Edits from "./Edits/Edits";

export default class Tasklist extends Component {
  state = {
    tasksArray: [
      {
        id: "ta1",
        name: "Calculus homework",
        owner: "samer",
        priority: "Urgent",
        category: "school",
        deadline: new Date("May 09, 2020"),
        done: false
      },
      {
        id: "ta2",
        name: "buy chicken",
        owner: "samer",
        priority: "Low",
        category: "market",
        deadline: new Date("May 10, 2020"),
        done: false
      },
      {
        id: "ta3",
        name: "E100",
        owner: "samer",
        priority: "Medium",
        category: "school",
        deadline: new Date("May 10, 2020"),
        done: false
      }
    ]
  };

  delete = id => {
    let stateUpdate = this.state.tasksArray;
    stateUpdate.forEach(elem => {
      if (elem.id === id && elem.done === false) {
        elem.done = true;
      }
    });
    this.setState({ tasksArray: stateUpdate });
  };

  deleteAll= ()=>{
    let stateUpdate = this.state.tasksArray;
    stateUpdate.forEach(elem => elem.done=true)
    this.setState({ tasksArray: stateUpdate });
  }

  render() {
    return (
      <div className="tasksList">
        {this.state.tasksArray.map(elem =>
          (this.props.cat === "All" || this.props.cat === elem.category) &&
          elem.done === false ? (
            <Task key={elem.id} obj={elem} delete={this.delete} />
          ) : (
            <Fragment></Fragment>
          )
        )}
        <Edits />
      </div>
    );
  }
}
