import React, { Component } from 'react'

export default class Tab extends Component {
    onClick = (event) =>{
        this.props.activate(this.props.id)
    }
    render() {
        return (
            <a onClick={this.onClick}  className={this.props.active}>
                <button
              className="delete is-small"
              aria-label="close"
              onClick={this.cancelAdd}
            ></button>
                {this.props.name}
            </a>
        )
    }
}
