import React, { Component } from "react";
import Tab from "./Tab/Tab";

class Tabs extends Component {
  state = {
    categoriesArray: [
      {
        id: "cat0",
        name: "All",
        active: true
      },
      {
        id: "cat1",
        name: "market",
        active: false
      },
      {
        id: "cat2",
        name: "school",
        active: false
      }
    ]
  };

  activate = id => {
    let stateUpdate = this.state.categoriesArray;
    stateUpdate.forEach(elem =>
      elem.id === id ? (elem.active = true) : (elem.active = false)
    );
    this.setState({ categoriesArray: stateUpdate });

    this.props.update()
  };

  render() {
    return (
      <p className="panel-tabs">
        {this.state.categoriesArray.map(elem => (
          <Tab
            key={elem.id}
            id={elem.id}
            name={elem.name}
            update={this.update}
            activate={this.activate}
            active={elem.active ? "is-active" : ""}
          />
        ))}
        <a>
          <em>+ Add Category</em>
        </a>
      </p>
    );
  }
}

export default Tabs;
