import React, { Component } from "react";
import Tab from "./Tab/Tab";

class Tabs extends Component {
  state = {
    categoriesArray: [
      {
        id: "cat1",
        name: "market",
        active:true
      },
      {
        id: "cat2",
        name: "school",
        active:false
      }
    ]
  };
  render() {
    return (
      <p className="panel-tabs">
        <a className="is-active">All</a>
        {this.state.categoriesArray.map(elem => (
            <Tab key={elem.key} name={elem.name} activate={elem.active?'is-active':''} />
        ))}
        <a><em>+ Add Category</em></a>
      </p>
    );
  }
}

export default Tabs;
